<script setup lang="ts">
import { computed } from 'vue'

// Props for customization
interface Props {
  color?: string
  useTailwind?: boolean
  opacity?: string | number
}

const props = withDefaults(defineProps<Props>(), {
  color: 'white',
  useTailwind: true,
  opacity: '20'
})

// Compute the dot class based on props
const dotClass = computed(() => {
  if (props.useTailwind) {
    // Use Tailwind classes
    return `bg-${props.color} opacity-${props.opacity}`
  }
  // If not using Tailwind, leave class empty and use inline styles
  return ''
})

// Compute inline styles for custom colors
const dotStyle = computed(() => {
  if (!props.useTailwind) {
    return {
      backgroundColor: props.color,
      opacity: typeof props.opacity === 'string' ? parseFloat(props.opacity) / 100 : props.opacity
    }
  }
  return {}
})
</script>